import java.util.Scanner;import java.util.ArrayList;import java.io.IOException;import org.apache.hadoop.io.IntWritable;import org.apache.hadoop.io.LongWritable;import org.apache.hadoop.io.Text;import org.apache.hadoop.mapreduce.Mapper;public class Hab278_Hw_3_Mapper    extends Mapper<LongWritable, Text, Text, Text> {  @Override  public void map(LongWritable key, Text value, Context context)      throws IOException, InterruptedException {    String line = value.toString();    Scanner scanner = new Scanner(line);    String page = scanner.next();    ArrayList<String> outboundLinks = new ArrayList<String>();    while(!scanner.hasNextDouble()){      outboundLinks.add(scanner.next());    }    double pageRank = scanner.nextDouble();    pageRank = pageRank / (double)outboundLinks.size();    String outboundLinksStr = "";    for(String outboundLink : outboundLinks){      context.write(new Text(outboundLink), new Text(page + " " + Double.toString(pageRank)));      outboundLinksStr = outboundLinksStr + outboundLink + " ";    }    context.write(new Text(page), new Text(outboundLinksStr) );  }}