import java.util.Scanner;import java.io.IOException;import org.apache.hadoop.io.IntWritable;import org.apache.hadoop.io.Text;import org.apache.hadoop.mapreduce.Reducer;public class Hab278_Hw_3_Reducer    extends Reducer<Text, Text, Text, Text> {  @Override  public void reduce(Text key, Iterable<Text> values, Context context)      throws IOException, InterruptedException {    String page = "";    String outLink = "";    double totalRank = 0;    Scanner scanner;    for (Text value : values) {      scanner = new Scanner(value.toString());      page = scanner.next();      if(scanner.hasNextDouble()){        totalRank = totalRank + scanner.nextDouble();      } else {        outLink = page;        while(scanner.hasNext())          outLink = outLink + " " + scanner.next();      }    }    context.write(key, new Text(outLink + " " + Double.toString(totalRank)));  }}